import React, { useEffect, useState } from 'react';

const FullScreenSections = () => {
  const sections = ['about', 'experience', 'projects', 'blog'];
  const [currentSection, setCurrentSection] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      sections.forEach((section, index) => {
        const element = document.getElementById(section);
        const rect = element.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          setCurrentSection(index);
          element.classList.add('animate');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [sections]);

  const handleScrollToSection = (index) => {
    const element = document.getElementById(sections[index]);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div>
      {sections.map((section, index) => (
        <div className="section" id={section} key={section}>
          <div>
            <h1>{section.charAt(0).toUpperCase() + section.slice(1)}</h1>
            <p>Details about my {section}...</p>
          </div>
        </div>
      ))}
      {currentSection > 0 && (
        <div className="arrow up" onClick={() => handleScrollToSection(currentSection - 1)}>&#x2191;</div>
      )}
      {currentSection < sections.length - 1 && (
        <div className="arrow down" onClick={() => handleScrollToSection(currentSection + 1)}>&#x2193;</div>
      )}
    </div>
  );
}

export default FullScreenSections;
