import React, { useEffect, useRef } from 'react';
import './FullScreenSections.css';

const FullScreenSections = () => {
  const sections = ['about', 'experience', 'projects', 'blog'];
  const sectionRefs = useRef(sections.map(() => React.createRef()));

  const handleScrollToSection = (index) => {
    if (index < sectionRefs.current.length) {
      const element = sectionRefs.current[index].current;
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  return (
    <div className="container">
      {sections.map((section, index) => (
        <div
          className="section"
          id={section}
          key={section}
          ref={sectionRefs.current[index]}
        >
          <div>
            <h1>{section.charAt(0).toUpperCase() + section.slice(1)}</h1>
            <p>Details about my {section}...</p>
            {index < sections.length - 1 && (
              <div className="arrow down" onClick={() => handleScrollToSection(index + 1)}>&#x2193;</div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default FullScreenSections;
