import React, { useEffect } from 'react';

const FullScreenSections = () => {
  const sections = ['about', 'experience', 'projects', 'blog'];

  useEffect(() => {
    const handleScroll = () => {
      sections.forEach((section) => {
        const element = document.getElementById(section);
        const rect = element.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          element.classList.add('animate');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div>
      <div className="section" id="about">
        <div>
          <h1>About Me</h1>
          <p>I'm passionate about data science, data analytics, machine learning, AI, and product management.</p>
        </div>
      </div>
      <div className="section" id="experience">
        <div>
          <h1>Experience</h1>
          <p>Details about my professional experience...</p>
        </div>
      </div>
      <div className="section" id="projects">
        <div>
          <h1>Projects</h1>
          <p>Details about my projects...</p>
        </div>
      </div>
      <div className="section" id="blog">
        <div>
          <h1>Blog</h1>
          <p>Read my latest articles...</p>
        </div>
      </div>
      <div className="arrow down" onClick={() => handleScrollToSection('experience')}>&#x2193;</div> {/* Down arrow */}
      <div className="arrow up" onClick={() => handleScrollToSection('about')}>&#x2191;</div> {/* Up arrow */}
    </div>
  );
}

export default FullScreenSections;
