import React, { useEffect, useRef } from 'react';
import './FullScreenSections.css';

const FullScreenSections = () => {
  const sections = ['about', 'experience', 'projects', 'blog'];
  const sectionRefs = useRef(sections.map(() => React.createRef()));

  useEffect(() => {
    const observerOptions = {
      threshold: 0.5
    };

    const observerCallback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate');
        } else {
          entry.target.classList.remove('animate');
        }
      });
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);

    sectionRefs.current.forEach(ref => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });

    const handleScroll = () => {
      const container = document.querySelector('.container');
      let closest = sectionRefs.current[0].current;
      let closestDist = Math.abs(container.scrollTop - closest.offsetTop);

      sectionRefs.current.forEach(ref => {
        const section = ref.current;
        const distance = Math.abs(container.scrollTop - section.offsetTop);
        if (distance < closestDist) {
          closest = section;
          closestDist = distance;
        }
      });

      closest.scrollIntoView({ behavior: 'smooth' });
    };

    const container = document.querySelector('.container');
    container.addEventListener('scroll', handleScroll);

    const handleMouseMove = (event) => {
      const character = document.querySelector('.character');
      const eyes = document.querySelectorAll('.eye');
      const mouseX = event.clientX;
      const mouseY = event.clientY;
      const characterRect = character.getBoundingClientRect();
      const characterX = characterRect.left + characterRect.width / 2;
      const characterY = characterRect.top + characterRect.height / 2;
      const angle = Math.atan2(mouseY - characterY, mouseX - characterX);

      eyes.forEach(eye => {
        eye.style.transform = `rotate(${angle}rad)`;
      });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      sectionRefs.current.forEach(ref => {
        if (ref.current) {
          observer.unobserve(ref.current);
        }
      });
      container.removeEventListener('scroll', handleScroll);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="container">
      <div className="character">
        <div className="eye left"></div>
        <div className="eye right"></div>
      </div>
      {sections.map((section, index) => (
        <div
          className="section"
          id={section}
          key={section}
          ref={sectionRefs.current[index]}
        >
          <div>
            <h1>{section.charAt(0).toUpperCase() + section.slice(1)}</h1>
            <p>Details about my {section}...</p>
            {index < sections.length - 1 && (
              <div className="arrow down">&#x2193;</div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default FullScreenSections;
